<?php

require '../Include/Config.php';

// This file is generated by Composer
require_once dirname(__FILE__) . '/../vendor/autoload.php';

use Slim\Factory\AppFactory;
use DI\Container;
use EcclesiaCRM\Slim\dependencies;

use EcclesiaCRM\Slim\Error\handlers;

$rootPath = str_replace('/external/index.php', '', $_SERVER['SCRIPT_NAME']);

// Instantiate the app
$container = new Container();

AppFactory::setContainer($container);

$app = AppFactory::create();

// Register the http cache middleware.
//$app->add( new Cache('private', 0) );

$app->setBasePath($rootPath . "/external");

// Set up
dependencies::install($container, true);

// error handlers
$handlers = new handlers($app);
$handlers->installHandlers();

// routes
require __DIR__ . '/routes/calendar.php';
require __DIR__ . '/routes/register.php';
require __DIR__ . '/routes/password.php';

// Run app
$app->run();
